  React.useEffect(() => {
    Axios.get(`${url}${query}`)
      .then((r) => {
        setResp(r.data);
        setNews(r.data.results);
      })
  }, [url, query]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Space Party</h1>
      </header>

      <main>
       <SearchBar
       searchTerm={searchTerm}
       setSearchTerm={setSearchTerm}
       setQuery={setQuery}
       /> 

       <Pagination
       resp={resp}
       url={url}
       setUrl={setUrl}
       />

       <Ordering
       setQuery={setQuery}
       />

       <div id="newslist">
          {news
            .map((element) => (
              <Card
                key={element.id}
                title={element.title}
                imgSrc={element.image_url}
                summary={element.summary}
                url={element.url}
              />
            ))
          }
        </div>
      </main>
