import './App.css';
import Card from './Card';
import Axios from "axios"
import React from 'react';

function App() {
  const [searchTerm, setSearchTerm] = React.useState("");
  const[query, setQuery] = React.useState("");
  const [news, setNews] = React.useState([]);


  const handleChange = event => {
    setSearchTerm(event.target.value);
    setQuery(`?search=${searchTerm}`)
  };

  const handleNext = event =>{
    setQuery()
  }

React.useEffect(() => {
    Axios.get(`https://api.spaceflightnewsapi.net/v4/articles/${query}`)
      .then((resp) => {
        setResp(resp.data);
        setNews(resp.data.results);
      })
  }, [query]);



  return (
    <div className="App">
      <header className="App-header">
        <h1>Space Party</h1>
      </header>

      <main>
        <input type="text" placeholder="search" value={searchTerm}
          onChange={handleChange} />

        <a href="#" onClick={handlePrevious} class="previous round">&#8249;</a>
        <a href="#" onClick={handleNext} class="next round">&#8250;</a>

        {news
          //.filter(element =>
            //(element.title.toLowerCase().includes(searchTerm) || element.summary.toLowerCase().includes(searchTerm)))
          .map((element) => (
            <Card
              key={element.id}
              title={element.title}
              imgSrc={element.image_url}
              summary={element.summary}
              url={element.url}
            />
          ))
        }
      </main>
      <footer>
        by Marta Trinc√£o
      </footer>
    </div>
  );
}

export default App;
